query SiteTitleQuery {
  site {
    siteMetadata {
      title
    }
  }
  allMarkdownRemark(sort: {fields: [frontmatter___order]}, filter: {frontmatter: {type: {eq: "page"}}}) {
    edges {
      node {
        fields {
          slug
        }
        frontmatter {
          date(formatString: "MMMM Do YYYY")
          title
          category
          type
          order
          visible
        }
      }
    }
  }
}

